import json
import webbrowser
import gachaMetadata
from utils import logger, gachaDataPath, gachaReportPath


gachaQueryTypeIds = gachaMetadata.gachaQueryTypeIds
gachaQueryTypeNames = gachaMetadata.gachaQueryTypeNames
gachaQueryTypeDict = gachaMetadata.gachaQueryTypeDict


def get_banner(gacha_type, detail):
    r5info = ""
    if detail[gacha_type]["5"] > 0:
        r5history = ""
        for rank5 in detail[gacha_type]["rank5logs"]:
            r5history += f"""<span style="margin-right: .5rem;">{rank5}</span>"""
        r5info = f"""
        <span>5星平均出货次数: { round(detail[gacha_type]["totalForRank5"] / detail[gacha_type]["5"], 2) }</span><br>
        <span>5星历史记录: </span>
        {r5history}"""

    return f"""
    <div class="col-md-12 col-lg-6 col-xl-6" style="border: 0 2rem;">
      <h2>{gachaQueryTypeDict[gacha_type]}<a style="font-size:10px">{detail[gacha_type]["start_time"]}</a></h2>
      <div class="table-responsive">
        <table style="display: inline-table; min-width: max-content;">
          <thead>
            <tr>
              <th>星级</th>
              <th>数量</th>
              <th style="cursor:help" title="物品数 / 总数">基础概率</th>
              <th style="cursor:help" title="(物品数 - 距上次) / 总数">综合概率</th>
              <th>距上次</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>5星</td>
              <td style="cursor:help" title="{detail[gacha_type]["items5str"]}">{detail[gacha_type]["5"]}</td>
              <td>{detail[gacha_type]["base_percent5"]}</td>
              <td>{detail[gacha_type]["total_percent5"]}</td>
              <td>{detail[gacha_type]["pity5"]}</td>
            </tr>
            <tr>
              <td>4星</td>
              <td style="cursor:help" title="{detail[gacha_type]["items4str"]}">{detail[gacha_type]["4"]}</td>
              <td>{detail[gacha_type]["base_percent4"]}</td>
              <td>{detail[gacha_type]["total_percent4"]}</td>
              <td>{detail[gacha_type]["pity4"]}</td>
            </tr>
            <tr>
              <td>3星</td>
              <td style="cursor:help" title="{detail[gacha_type]["items3str"]}">{detail[gacha_type]["3"]}</td>
              <td>{detail[gacha_type]["base_percent3"]}</td>
              <td>&nbsp;</td>
              <td>&nbsp;</td>
            </tr>
            <tr>
              <td>总计</td>
              <td style="cursor:help" title="{detail[gacha_type]["itemsstr"]}">{detail[gacha_type]["total"]}</td>
              <td>&nbsp;</td>
              <td>&nbsp;</td>
              <td>&nbsp;</td>
            </tr>
          </tbody>
        </table>
      </div>
      {r5info}
    </div>
"""


def main():
    gachaLog = {}
    with open(gachaDataPath, "r", encoding="utf-8") as f:
        logger.debug("打开文件: {}", gachaDataPath)
        j = json.load(f)
        gachaLog = j["gachaLog"]

    def percent(a, b):
        if b == 0:
            return "0.0%"
        else:
            return f"{round(a/b*100, 2)}%"

    detail = {}
    for key in gachaLog:
        banner = gachaLog[key]
        logger.debug("处理卡池: {} {} 数量: {}", key, gachaQueryTypeDict.get(key, key), len(banner))
        banner.reverse()
        detail[key] = {
            "5": 0,
            "4": 0,
            "3": 0,
            "total": len(banner),
            "pity5": 0,
            "pity4": 0,
            "items5": {},
            "items4": {},
            "items3": {},
            "items": {"w3": 0, "w4": 0, "w5": 0, "c4": 0, "c5": 0},
            "itemsstr": "",
            "items5str": "",
            "items4str": "",
            "items3str": "",
            "rank5logs": [],
            "start_time": "",
            "base_percent5": "",
            "base_percent4": "",
            "base_percent3": "",
            "total_percent5": "",
            "total_percent4": "",
        }
        count = 0
        pity = 0
        for log in banner:
            count += 1
            pity += 1
            log["count"] = count
            log["pity"] = pity
            if log["rank_type"] == "5":
                pity = 0
        for log in banner:
            if detail[key]["start_time"] == "":
                detail[key]["start_time"] = " 开始于: " + log.get("time")
            rank_type = log.get("rank_type")
            name = log.get("name")
            pity = log.get("pity")
            if rank_type == "5":
                detail[key]["items5"][name] = 0
                detail[key]["rank5logs"].append(name + "@" + str(pity))
            if rank_type == "4":
                detail[key]["items4"][name] = 0
            if rank_type == "3":
                detail[key]["items3"][name] = 0
        for log in banner:
            rank_type = log["rank_type"]
            name = log["name"]
            item_type = log["item_type"]
            if rank_type == "5":
                detail[key]["5"] += 1
                detail[key]["items5"][name] += 1
                detail[key]["pity5"] = 0
                detail[key]["pity4"] += 1
                if item_type == "武器":
                    detail[key]["items"]["w5"] += 1
                if item_type == "角色":
                    detail[key]["items"]["c5"] += 1
            if rank_type == "4":
                detail[key]["4"] += 1
                detail[key]["items4"][name] += 1
                detail[key]["pity5"] += 1
                detail[key]["pity4"] = 0
                if item_type == "武器":
                    detail[key]["items"]["w4"] += 1
                if item_type == "角色":
                    detail[key]["items"]["c4"] += 1
            if rank_type == "3":
                detail[key]["3"] += 1
                detail[key]["items3"][name] += 1
                detail[key]["pity5"] += 1
                detail[key]["pity4"] += 1
                if item_type == "武器":
                    detail[key]["items"]["w3"] += 1
        key_map = {"w3": "3星武器", "w4": "4星武器", "w5": "5星武器", "c4": "4星角色", "c5": "5星角色"}
        for k in detail[key]["items"]:
            keyName = key_map[k]
            detail[key]["itemsstr"] += keyName + "x" + str(detail[key]["items"][k]) + "\n"
        for k in detail[key]["items5"]:
            detail[key]["items5str"] += k + "x" + str(detail[key]["items5"][k]) + "\n"
        for k in detail[key]["items4"]:
            detail[key]["items4str"] += k + "x" + str(detail[key]["items4"][k]) + "\n"
        for k in detail[key]["items3"]:
            detail[key]["items3str"] += k + "x" + str(detail[key]["items3"][k]) + "\n"

        detail[key]["totalForRank5"] = detail[key]["total"] - detail[key]["pity5"]
        detail[key]["totalForRank4"] = detail[key]["total"] - detail[key]["pity4"]
        detail[key]["base_percent5"] = percent(detail[key]["5"], detail[key]["total"])
        detail[key]["base_percent4"] = percent(detail[key]["4"], detail[key]["total"])
        detail[key]["base_percent3"] = percent(detail[key]["3"], detail[key]["total"])
        detail[key]["total_percent5"] = percent(detail[key]["5"], detail[key]["totalForRank5"])
        detail[key]["total_percent4"] = percent(detail[key]["4"], detail[key]["totalForRank4"])

    # with open("details.json","w",encoding="utf-8") as f:
    #     json.dump(detail,f,ensure_ascii=False,indent=4)
    logger.debug("处理完成")
    logger.debug(detail)

